DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) NOT NULL COMMENT '作者',
  `cat_id` int(11) NOT NULL COMMENT '分类',
  `created_at` int(11) DEFAULT NULL COMMENT '添加时间',
  `updated_at` int(11) DEFAULT NULL COMMENT '更新时间',
  `view_times` int(11) DEFAULT NULL COMMENT '阅读量',
  `slug` varchar(128) DEFAULT NULL COMMENT 'Slug',
  `enable_comment` bit(1) DEFAULT b'0' COMMENT '是否可以评论',
  `doc_type` varchar(32) DEFAULT 'article' COMMENT '类型',
  `title` varchar(128) NOT NULL COMMENT '标题',
  `cover` varchar(128) DEFAULT NULL COMMENT '图片',
  `keywords` varchar(128) DEFAULT NULL COMMENT '关键字',
  `description` varchar(255) DEFAULT NULL COMMENT '摘要',
  `content` text COMMENT '内容',
  PRIMARY KEY (`id`),
  UNIQUE KEY `slug` (`slug`)
) ENGINE=InnoDB AUTO_INCREMENT=32 DEFAULT CHARSET=utf8mb4 COMMENT='文章';

